mapper.select:
select count(*)
from refresh_token
where rt_key = #{rt_key}

mapper.update:
update refresh_token
set refreshtoken_value=#{refreshtoken_value}, refreshtoken_expire = #{refreshtoken_expire}
where rt_key = #{rt_key}

mapper.insert:
update refresh_token
set accesstoken_value=#{accesstoken_value}
where rt_key = #{rt_key}
